        <p>Genre: {event.classificationBean[0].genre.name}</p>
      </div>
      <div>
        <h3>Venue</h3>
        <p>Name: {event.embeddedBean.venuesBeans[0].name}</p>
        <p>City: {event.embeddedBean.venuesBeans[0].city.name}</p>
        <p>State: {event.embeddedBean.venuesBeans[0].state.name}</p>
      </div>
      <div>
        <h3>Sales Information</h3>
        <p>Start Date: {event.salesBean.pobj.startDateTime}</p>
        <p>End Date: {event.salesBean.pobj.endDateTime}</p>
      </div>
      <img src={event.imageBeans[0].url} alt={event.name} />
      {/* Add more event details as needed */}
    </div>
  );

}

