import {
  ShoppingCartOutlined,
} from "@mui/icons-material";
import React from "react";

const iconStyle = 'h-[40px] w-[40px] rounded-full bg-white flex items-center justify-center m-3 hover:bg-[#894af3] hover:text-white hover:scale-[1.1] ease-in duration-100 cursor-pointer'

const Product = ({ item }) => {
  return (
    <div className=" flex-1 min-w-[350px] m-2 overflow-hidden rounded-md shadow-lg">
      <img src={item.src} alt="product" />
      <h3 className="text-center text-xl font-semibold mt-2">Event Name</h3>
      <p className="text-center text-gray-600 pl-5 pr-5">Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s</p>
      <div className="flex justify-between m-5">
        <div className={iconStyle}>
          <ShoppingCartOutlined />
        </div>
        <button className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded">
          View Details
        </button>
      </div>
    </div>
  );
};

export default Product;
