const Product = ({ item }) => {
  return (
    <div className="flex-1 min-w-[280px] min-h-[350px] m-2 overflow-hidden rounded-md shadow-lg">
      <img src={item.imageUrl} alt={item.eventName} />
      <h3 className="text-center text-xl font-semibold mt-2">{item.eventName}</h3>
      <p className="text-center text-gray-600 pl-5 pr-5">{item.description}</p>
      <div className="flex justify-between m-5">
        <div className={iconStyle}>
          <ShoppingCartOutlined />
        </div>
        <button className="bg-blue-500 hover:bg-blue-600 text-white m-2 px-4 py-2 rounded">
          View Details
        </button>
      </div>
    </div>
  );
};

export default Product;
