import React from "react";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import NavBar from "../components/NavBar";
import Announce from "../components/Announce";
import Footer from "../components/Footer";

function EventDetail() {

  const { eventId } = useParams();

  const [eventDetails, setEventDetails] = useState({});

  useEffect(() => {

    const fetchEventDetails = async () => {
      try {
        const response = await fetch(`http://localhost:8084/event/get/event/byid/` + eventId);
        if (!response.ok) {
          throw new Error("Failed to fetch event details");
        }
        // const eventData = await response.json();
        // setEventDetails(eventData);
        setEventDetails(Array.isArray(response.data.events) ? response.data.events : []);
      } catch (error) {
        console.error("Error fetching event details:", error);
      }
    };
    fetchEventDetails();
  }, [eventId]);

  return<div>
    <Announce />
    <NavBar />
    <div className="flex justify-center m-6 mobile:flex-col mobile:mt-4 mobile:p-3">
      <div className="flex-[1.1] flex items-center justify-center ">
            <img
              src="eventDetails.imageBeans.find((image) => image.width === 1024)?.url"
              className="event_img rounded-lg shadow-lg"
              alt="event_image"
            />
      </div>
      <div className="flex-[1] flex flex-col items-start ml-10 justify-items-center mt-5 mobile:items-center">
        <h1 className="title text-[40px] mobile:text-[30px]">
          {eventDetails.name}
        </h1>
        <p className="discription pr-[4rem] text-justify mt-4 mobile:pr-0">
          {eventDetails.description}
        </p>
        <p className="location pr-[4rem] text-justify mt-4 mobile:pr-0">
          
        </p>
        <p className="date pr-[4rem] text-justify mt-4 mobile:pr-0">
          
        </p>
        <div className="flex flex-col place-self-start">
          <p className="mt-7 text-3xl">
            Price: <b></b>
          </p>

        </div>

        <button className="text-white bg-[#8a4af3] rounded-md shadow-md mt-[30px] p-3">
          Add to Cart
        </button>
        <button className="text-white bg-[#249149] rounded-md shadow-md mt-[30px] p-3">
          Request for Stall
        </button>
      </div>
    </div>
    <Footer />

  </div>;
}

    export default EventDetail;
